/****** Script for SelectTopNRows command from SSMS  ******/
SELECT [Indra].[dbo].[GCGT_RE_READING].*
  FROM [Indra].[dbo].[GCCD_RELATIONSHIP],[Indra].[dbo].[GCCOM_PAYMENT_FORM],[Indra].[dbo].[GCCOM_CONTRACTED_SERVICE],[Indra].[dbo].[GCCOM_SECTOR_SUPPLY],[Indra].[dbo].[GCGT_RE_MEASUREMENT_POINT],[Indra].[dbo].[GCGT_RE_READING]
	WHERE [Indra].[dbo].[GCCD_RELATIONSHIP].[COD_CUSTOMER]=[Indra].[dbo].[GCCOM_PAYMENT_FORM].[ID_CUSTOMER] AND [Indra].[dbo].[GCCOM_CONTRACTED_SERVICE].[ID_PAYMENT_FORM]=[Indra].[dbo].[GCCOM_PAYMENT_FORM].[ID_PAYMENT_FORM] AND[Indra].[dbo].[GCCOM_CONTRACTED_SERVICE].[ID_SECTOR_SUPPLY]=[Indra].[dbo].[GCCOM_SECTOR_SUPPLY].[ID_SECTOR_SUPPLY] AND [Indra].[dbo].[GCCOM_SECTOR_SUPPLY].[ID_SECTOR_SUPPLY] =[Indra].[dbo].[GCGT_RE_MEASUREMENT_POINT].[ID_SECTOR_SUPPLY] AND [Indra].[dbo].[GCGT_RE_MEASUREMENT_POINT].[ID_MEASURING_POINT]=[Indra].[dbo].[GCGT_RE_READING].[ID_MEASURING_POINT] AND [Indra].[dbo].[GCCD_RELATIONSHIP].[CUSTOMER_TYPE]='TYPCUS0002'